package doubleLongFloat;

public class DoubleLongFloat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 50;
		long b = 99999999999999L; // after a certain digit must put an L at the end
		float c = 2.5F; // can use decimals but have to use F
		double d = 55555555555555555555555555555555555555.5555555555555555555555555555; // can use very long decimals
																						// and DONT have to use F

		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);

	}

}
